require 'rspec'
require_relative '_002_transcribe_dna_to_rna'

describe Rosalind::DnaToRna do

  let(:sample_string) {'GATGGAACTTGACTACGTAAATT'}

  let(:challenge_string) do
    ""
  end

  it 'returns the expected output' do
    result = described_class.transcribe(dna_string: sample_string)

    expect(result).to eq('GAUGGAACUUGACUACGUAAAUU')
  end

  it 'solves the challenge' do
    result = described_class.transcribe(dna_string: challenge_string)

    expect(result).to eq('')
  end
end
